package experiment;

import static org.junit.jupiter.api.Assertions.assertEquals;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.CsvSource;

public class TestNextDate {

        @ParameterizedTest
        @CsvSource({
                "1, 0, 2020, invalid Input Date",
                "0, 1, 2020, invalid Input Date",
                "13, 1, 2020, invalid Input Date",
                "1, 1, 1799, invalid Input Date",
                "1, 1, 10000, invalid Input Date"
        })
        public void testInvalidDates(int month, int day, int year, String expectedOutput) {
            NextDate nextDate = new NextDate(month, day, year);
            assertEquals(expectedOutput, nextDate.run(month, day, year));
        }

        @ParameterizedTest
        @CsvSource({
                "1, 30, 2020, 1/31/2020",
                "1, 31, 2020, 2/1/2020"
        })
        public void testThirtyOneDayMonth(int month, int day, int year, String expectedOutput) {
            NextDate nextDate = new NextDate(month, day, year);
            assertEquals(expectedOutput, nextDate.run(month, day, year));
        }

        @ParameterizedTest
        @CsvSource({
                "4, 29, 2020, 4/30/2020",
                "4, 30, 2020, 5/1/2020",
                "4, 31, 2020, Invalid Input Date"
        })
        public void testThirtyDayMonth(int month, int day, int year, String expectedOutput) {
            NextDate nextDate = new NextDate(month, day, year);
            assertEquals(expectedOutput, nextDate.run(month, day, year));
        }

        @ParameterizedTest
        @CsvSource({
                "12, 30, 2020, 12/31/2020",
                "12, 31, 2021, Invalid Input Date",
                "12, 31, 2020, 12/31/2021"
        })
        public void testDecember(int month, int day, int year, String expectedOutput) {
            NextDate nextDate = new NextDate(month, day, year);
            assertEquals(expectedOutput, nextDate.run(month, day, year));
        }

        @ParameterizedTest
        @CsvSource({
                "2, 27, 2020, 2/28/2020",
                "2, 28, 2020, 2/29/2020",
                "2, 28, 2020, 2/29/2020",
                "2, 29, 2020, 3/1/2020",
                "2, 29, 2021, Invalid Input Date",
                "2, 30, 2020, Invalid Input Date"
        })
        public void testFebruary(int month, int day, int year, String expectedOutput) {
            NextDate nextDate = new NextDate(month, day, year);
            assertEquals(expectedOutput, nextDate.run(month, day, year));
        }

    

}
